// src/components/Form.jsx
import React, { useState } from "react";
import emailjs from "emailjs-com";

export const Form = () => {
  const [formData, setFormData] = useState({
    role: "",
    appCategory: [],
    name: "",
    email: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");
  const SEND_EMAIL = false;

  const roles = [
    "Professor",
    "Scientist",
    "Engineer",
    "Post Grad",
    "Graduate Student",
  ];

  const appCategories = [
    "Continuous Flow Chemistry",
    "Reactor Design and Development",
    "Reaction/Process Monitoring",
    "Purity Monitoring",
    "Concentration Monitoring",
    "Blending Verification",
    "Complex Component Mixtures Quantification",
  ]; // Add more as needed

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleCheckboxChange = (category) => {
    setFormData((prev) => {
      const updated = prev.appCategory.includes(category)
        ? prev.appCategory.filter((c) => c !== category)
        : [...prev.appCategory, category];
      return { ...prev, appCategory: updated };
    });
  };

  const isFormValid =
    formData.role &&
    formData.appCategory.length > 0 &&
    formData.name &&
    formData.email;

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isFormValid) return;

    setIsSubmitting(true);
    setSuccessMessage("");

    const formDataToSend = {
      role: formData.role,
      appCategory: formData.appCategory.join(", "),
      name: formData.name,
      email: formData.email,
    };

    try {
      //   await emailjs.send(
      //     "service_glye7pm",
      //     "template_79hve7b",
      //     formDataToSend,
      //     "Q0syV6H_5pAHvwxU9"
      //   );
      if (SEND_EMAIL) {
        await emailjs.send(
          "service_glye7pm",
          "template_79hve7b",
          formDataToSend,
          "Q0syV6H_5pAHvwxU9"
        );
      } else {
        console.log("Email sending skipped during testing.");
      }

      setSuccessMessage(
        "Your form has been successfully submitted! Email sending skipped during testing."
      );
      setTimeout(() => {
        setSuccessMessage("");
        window.location.replace("/form");
      }, 3000);
    } catch (error) {
      console.error("EmailJS error:", error);
      setSuccessMessage("Something went wrong. Please try again.");
    }

    setIsSubmitting(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 py-28 px-4">
      <div className="max-w-3xl mx-auto mt-40 p-12 bg-white shadow-2xl rounded-3xl">
        <h2 className="text-4xl font-bold mb-10 text-center text-gray-900">
          Form
        </h2>
        <form onSubmit={handleSubmit} className="space-y-10">
          {/* Role */}
          <div>
            <label className="block text-2xl font-medium text-gray-800 mb-8">
              What is your role? <span className="text-red-600">*</span>
            </label>
            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
              className="w-full border border-gray-300 rounded-xl p-4 text-2xl font-medium text-gray-600"
              required
            >
              <option value="">-- Select your role --</option>
              {roles.map((r) => (
                <option key={r} value={r}>
                  {r}
                </option>
              ))}
            </select>
          </div>

          {/* Application Categories */}
          <div>
            <label className="block text-2xl font-medium text-gray-800 mb-8">
              Application Categories <span className="text-red-600">*</span>
            </label>
            <div className="mt-4 space-y-4">
              {appCategories.map((category) => (
                <label
                  key={category}
                  className="flex items-center space-x-3 pl-0 p-2 rounded hover:bg-gray-50"
                >
                  <input
                    type="checkbox"
                    checked={formData.appCategory.includes(category)}
                    onChange={() => handleCheckboxChange(category)}
                    className="h-5 w-5 text-red-600"
                  />
                  <span className="text-2xl text-gray-800">{category}</span>
                </label>
              ))}
            </div>
          </div>
          <div className="mt-12"></div>
          {/* Name and Email */}
          <div>
            <label className="block text-2xl font-medium text-gray-800 mb-8">
              What is your name and official email address?{" "}
              <span className="text-red-600">*</span>
            </label>
            <input
              type="text"
              name="name"
              placeholder="Your full name"
              value={formData.name}
              onChange={handleChange}
              className="w-full mb-4 border border-gray-300 rounded-xl p-4 text-xl"
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Company or student email"
              value={formData.email}
              onChange={handleChange}
              className="w-full border border-gray-300 rounded-xl p-4 text-xl"
              required
              pattern=".+@([a-zA-Z0-9-]+\.)+[a-zA-Z]{2,}"
              title="Kindly use your company or university email address."
            />
          </div>

          {/* Success Message */}
          {successMessage && (
            <div className="text-center text-green-700 font-semibold text-2xl">
              {successMessage}
            </div>
          )}

          {/* Submit */}
          <div className="pt-6 text-right">
            <button
              type="submit"
              disabled={!isFormValid || isSubmitting}
              className="px-10 py-4 bg-red-800 text-white text-2xl rounded-xl hover:bg-red-900 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isSubmitting ? "Submitting..." : "Submit"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Form;
