import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

export const ProductFeature = () => {
  const [inView, setInView] = useState(false);
  const ref = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      const element = ref.current;
      if (element) {
        const elementPosition =
          element.getBoundingClientRect().top + window.scrollY;
        const scrollPosition = window.scrollY + window.innerHeight;
        if (scrollPosition > elementPosition + 100) {
          setInView(true);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const goToReactionMonitoring = () => {
    navigate("/reaction-monitoring");
  };

  const goToRTD = () => {
    navigate("/rtd");
  };

  return (
    <div id="productFeature" className="text-center">
      <div className="container">
        {/* Title for Featured Products */}
        <h3>Featured Products</h3>

        <div className="row">
          {/* Reaction Monitoring Product */}
          <div className="col-12 col-md-4 mb-4">
            <div className="products">
              <div className={`feature-box ${inView ? "fadeInUp" : ""}`}>
                <div className="img-container mb-3">
                  <img
                    src="img/portfolio/ReactionMonitoring.png"
                    className="img-responsive"
                    alt="Reaction Monitoring"
                    onClick={goToReactionMonitoring}
                  />
                </div>
                <h4
                  style={{
                    cursor: "pointer",
                    color: "#fff",
                    textDecoration: "underline",
                  }}
                  onClick={goToReactionMonitoring}
                >
                  Reaction Monitoring
                </h4>
              </div>
            </div>
          </div>

          {/* RTD Product */}
          <div className="col-12 col-md-4 mb-4">
            <div className="products">
              <div className={`feature-box ${inView ? "fadeInUp" : ""}`}>
                <div className="img-container mb-3">
                  <img
                    src="img/portfolio/RTD.png"
                    className="img-responsive"
                    alt="RTD"
                    onClick={goToRTD}
                  />
                </div>
                <h4
                  style={{
                    cursor: "pointer",
                    color: "#fff",
                    textDecoration: "underline",
                  }}
                  onClick={goToRTD}
                >
                  Residence Time Distribution (RTD)
                </h4>
              </div>
            </div>
          </div>

          {/* Third Product Placeholder (Uncomment and adjust as needed) */}
          {/* <div className="col-12 col-md-4 d-flex justify-content-center align-items-stretch mb-4">
            <div className="products">
              <div className={`feature-box ${inView ? "fadeInUp" : ""}`}>
                <div className="img-container mb-3">
                  <img
                    src="img/portfolio/YourThirdProduct.png"
                    className="img-responsive"
                    alt="Your Third Product"
                    onClick={() => navigate("/your-third-product")}
                  />
                </div>
                <h4
                  style={{
                    cursor: "pointer",
                    color: "#fff",
                    textDecoration: "underline",
                  }}
                  onClick={() => navigate("/your-third-product")}
                >
                  Your Third Product
                </h4>
              </div>
            </div>
          </div> */}
        </div>
      </div>
    </div>
  );
};
