import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

export const ProductFeature = (props) => {
  const [inView, setInView] = useState(false);
  const ref = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const element = ref.current;
      if (element) {
        const elementPosition =
          element.getBoundingClientRect().top + window.scrollY;
        const scrollPosition = window.scrollY + window.innerHeight;
        if (scrollPosition > elementPosition + 100) {
          // Adjust threshold as needed
          setInView(true);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div id="productFeature" className="text-center">
      <div className="container">
        <div className="col-md-12" ref={ref}>
          <div className={`${inView ? "animated" : ""}`}>
            <div className="spectrometer">
              <h3>Product Feature</h3>

              <div className="col-12 col-md-6">
                <div className="img-container">
                  <img
                    src="img/portfolio/ReactionMonitoring.png"
                    className="img-responsive"
                    alt="Reaction Monitoring"
                    style={{ cursor: "pointer" }}
                    onClick={goToReactionMonitoring}
                  />
                </div>
              </div>
              <div className="col-12 col-md-6">
                <h4
                  style={{
                    cursor: "pointer",
                    color: "#007bff",
                    textDecoration: "underline",
                  }}
                  onClick={goToReactionMonitoring}
                >
                  Reaction Monitoring
                </h4>
                <p>
                  Click to explore more about how our technology enables
                  reaction monitoring in real time.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
