import { useState, useRef } from "react";

export const ProductIntroVideo = (props) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const videoRef = useRef(null);

  const handlePlay = () => {
    setIsPlaying(true); // Hide the thumbnail
    videoRef.current.play(); // Play the video
  };

  return (
    <div id="productIntroVideo">
      <div className="container">
        <div className="col-md-12">
          {/* Thumbnail shown before video starts */}
          {!isPlaying && (
            <div className="video-thumbnail" onClick={handlePlay}>
              <img
                src={`${process.env.PUBLIC_URL}/videoThumbnail.jpg`} // Path to your thumbnail image
                alt="Product Video Thumbnail"
                className="thumbnail-image"
              />
              <button className="play-button">â–¶</button>{" "}
              {/* Custom play button */}
            </div>
          )}
          {/* Video element */}
          <video
            ref={videoRef}
            className={`product-video ${isPlaying ? "active" : "hidden"}`} // Show video only after play
            controls
          >
            <source
              src={`${process.env.PUBLIC_URL}/productVideo.mp4`}
              type="video/mp4"
            />
          </video>
        </div>
      </div>
    </div>
  );
};
