import React, { useEffect, useState, useRef } from "react";

export const InlineMonitoring = (props) => {
  const [inView, setInView] = useState(false);
  const ref = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const element = ref.current;
      if (element) {
        const elementPosition =
          element.getBoundingClientRect().top + window.scrollY;
        const scrollPosition = window.scrollY + window.innerHeight;
        if (scrollPosition > elementPosition + 100) {
          setInView(true);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div id="inlineMonitoring" className="text-center">
      <div className="container">
        <div className="col-md-12" ref={ref}>
          <div className={`${inView ? "animated" : ""}`}>
            <div className="spectroscopy">
              <h3>INLINE PROCESS MONITORING</h3>
              <div className="spacer"></div>
              <p>
                {" "}
                Process industries such as (pharmaceutical, specialty chemicals,
                petrochemicals, chemicals, food and beverage) industries using
                liquid chemicals in their processes, can use our spectrometer to
                monitor reactions, reduce cycle times, maximize yield, reduce
                wastes, improve sustainability, control quality and scale up
                reactions.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
