// src/components/Form.jsx
import React, { useState } from "react";

export const Form = () => {
  const [formData, setFormData] = useState({
    role: "",
    appCategory: [],
    name: "",
    email: "",
  });

  const roles = [
    "Professor",
    "Scientist",
    "Engineer",
    "Post Grad",
    "Graduate Student",
  ];

  const appCategories = [
    "Continuous Flow Chemistry",
    "Reactor Design and Development",
    "Reaction/Process Monitoring",
    "Purity Monitoring",
    "Concentration Monitoring",
    "Blending Verification",
    "Complex Component Mixtures Quantification",
  ]; // Add more as needed

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleCheckboxChange = (category) => {
    setFormData((prev) => {
      const alreadySelected = prev.appCategory.includes(category);
      const updatedCategories = alreadySelected
        ? prev.appCategory.filter((c) => c !== category)
        : [...prev.appCategory, category];
      return { ...prev, appCategory: updatedCategories };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted:", formData);
    alert("Thank you for your submission!");
  };

  return (
    <div className="max-w-3xl mx-auto p-8 bg-white shadow-xl rounded-xl mt-10">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Quick Form</h2>
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Role */}
        <div>
          <label className="block text-lg font-medium text-gray-700 mb-2">
            What is your role?
          </label>
          <select
            name="role"
            value={formData.role}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-lg p-3"
            required
          >
            <option value="">-- Select your role --</option>
            {roles.map((r) => (
              <option key={r} value={r}>
                {r}
              </option>
            ))}
          </select>
        </div>

        {/* Application Categories */}
        <div>
          <label className="block text-lg font-medium text-gray-700 mb-2">
            Application Categories
          </label>
          <div className="flex flex-wrap gap-4">
            {appCategories.map((category) => (
              <label key={category} className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={formData.appCategory.includes(category)}
                  onChange={() => handleCheckboxChange(category)}
                  className="h-4 w-4 text-red-600"
                />
                <span>{category}</span>
              </label>
            ))}
          </div>
        </div>

        {/* Name and Email */}
        <div>
          <label className="block text-lg font-medium text-gray-700 mb-2">
            What is your name and official email address?
          </label>
          <input
            type="text"
            name="name"
            placeholder="Your full name"
            value={formData.name}
            onChange={handleChange}
            className="w-full mb-4 border border-gray-300 rounded-lg p-3"
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Company or student email"
            value={formData.email}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-lg p-3"
            required
            pattern=".+@(edu|ac|org|com|co)\..+"
            title="Use an official email, not personal."
          />
        </div>

        {/* Submit */}
        <div className="pt-4">
          <button
            type="submit"
            className="px-6 py-3 bg-red-800 text-white rounded-lg hover:bg-red-900 text-xl"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default Form;
