import React, { useEffect, useState, useRef } from "react";

export const ProductSpecification = (props) => {
  const [inView, setInView] = useState(false);
  const ref = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const element = ref.current;
      if (element) {
        const elementPosition =
          element.getBoundingClientRect().top + window.scrollY;
        const scrollPosition = window.scrollY + window.innerHeight;
        if (scrollPosition > elementPosition + 100) {
          setInView(true);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div id="productSpecification" className="text-center">
      <div className="container">
        <div className="col-md-12" ref={ref}>
          <div className={`${inView ? "animated" : ""}`}>
            <div className="productTitle">
              <h3>PRODUCT SPECIFICATIONS</h3>
            </div>
            <div className="spacer"></div>
            <div className="productInfo">
              <table className="productTable">
                <thead>
                  <tr>
                    <th>Specification</th>
                    <th>Details</th>
                  </tr>
                </thead>
                <tbody>
                  {[
                    {
                      specification: "Wavenumber Range",
                      details: "900 cm⁻¹ to 1800 cm⁻¹",
                    },
                    { specification: "Wavenumber", details: "900 - 1800" },
                    { specification: "Wavenumber", details: "900 - 1800" },
                    { specification: "Wavenumber", details: "900 - 1800" },
                    { specification: "Wavenumber", details: "900 - 1800" },
                    { specification: "Wavenumber", details: "900 - 1800" },
                    { specification: "Wavenumber", details: "900 - 1800" },
                    { specification: "Wavenumber", details: "900 - 1800" },
                    { specification: "Wavenumber", details: "900 - 1800" },
                    { specification: "Wavenumber", details: "900 - 1800" },
                  ]}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
