import React, { useEffect, useState, useRef } from "react";

export const Projects = (props) => {
  const [inView, setInView] = useState(false);
  const ref = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const element = ref.current;
      if (element) {
        const elementPosition =
          element.getBoundingClientRect().top + window.scrollY;
        const scrollPosition = window.scrollY + window.innerHeight;
        if (scrollPosition > elementPosition + 100) {
          // Adjust threshold as needed
          setInView(true);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);
  return (
    <div id="projects" className="text-center">
      <div className="container">
        <div className="col-md-12" ref={ref}>
          {props.data
            ? props.data.map((d, i) => (
                <div key={`${d.title}-${i}`} className="projects-item">
                  <h3 className={`${inView ? "animated" : ""}`}>{d.title}</h3>
                  <div className="spacer"></div>
                  <p>
                    <ul>
                      <li
                        className={`${inView ? "fadeInLeft" : ""}`}
                        style={{ animationDelay: `${i * 0.5}s` }}
                      >
                        In under six months, our lean but highly skilled team
                        developed a fully functional prototypeâ€”from chip design
                        and AI algorithms to integrated hardware and software.
                      </li>
                      <div style={{ height: "0.5em" }}></div>
                      <li
                        className={`${inView ? "fadeInRight" : ""}`}
                        style={{ animationDelay: `${i * 0.5 + 0.5}s` }}
                      >
                        We are now preparing the next product in our lineup and
                        look forward to showcasing it with our partners at
                        leading industry conferences.
                      </li>
                      <div style={{ height: "0.5em" }}></div>
                      {/* <li
                        className={`${inView ? "fadeInLeft" : ""}`}
                        style={{ animationDelay: `${i * 0.5 + 1}s` }}
                      >
                        <span class="highlight">Stay tuned</span> for our launch
                        with <span class="highlight">our partners</span> at{" "}
                        <span class="highlight">top conferences</span>.
                      </li> */}
                    </ul>
                  </p>
                </div>
              ))
            : "Loading..."}
        </div>
      </div>
    </div>
  );
};
