import React, { useRef, useEffect } from "react";
import "../css/productIntroVideo.css";

export const ProductIntroVideo = (props) => {
  const videoRef = useRef(null);

  useEffect(() => {
    const video = videoRef.current;

    if (video) {
      // Wait until metadata is loaded to set currentTime
      const handleLoadedMetadata = () => {
        video.currentTime = 1; // Start the video at 1 second
      };

      video.addEventListener("loadedmetadata", handleLoadedMetadata);

      // Cleanup event listener on unmount
      return () => {
        video.removeEventListener("loadedmetadata", handleLoadedMetadata);
      };
    }
  }, []);
  return (
    <div id="productIntroVideo">
      <div className="container">
        <div className="col-md-12">
          <h2 className="video-header">Production Introduction Video</h2>
          <video className="product-video" controls>
            <source
              src={`${process.env.PUBLIC_URL}/productVideo.mp4`}
              type="video/mp4"
            />
          </video>
        </div>
      </div>
    </div>
  );
};
